mapper.select:
select count(distinct user_id)
  from lottery_user_prize_record
where lottery_id=#{0}

select count(*)
  from lottery_user_prize_record
where lottery_id=#{0}

SELECT count(*) FROM lottery_user_prize_record a left join user_stock_coupon b on a.stock_id = b.stock_id WHERE b.status in
#{item}
AND a.prize_id in (select id from shop_conf_lottery_prize where lottery_id = #{id} AND prize_type = 'COUPON') AND a.lottery_id = #{id}
